import { Router } from "express";
import { UserController } from "../controllers/userController";
import models from '../database/models';

const { User } = models;

const router = Router();
/**
*  @swagger
*  /users:
*   get:
*    description: Get a list of all users
*    responses:
*      '200':
*        description: A successful response.
*/
router.get('/', UserController.getUsers);

/**
*  @swagger
*  /users/add:
*   post:
*    description: Add a user
*    responses:
*      '200':
*        description: A successful response.
*/
router.post('/add', UserController.addUser);
router.put('/:id', UserController.updateUser);

export default router;

import { create } from 'express-handlebars';
import models from '../database/models';

const { User } = models;

export class UserController {
  static async getUsers(req, res) {
    try {
      const allUsers = await User.findAll();

      res.status(200).json({ message: allUsers });
    } catch (error) {
      res.status(400).json(`Error: ${error}`);
    }
  };

  static async addUser(req, res) {
    try {
      // const { name, email } = req.body;
      console.log(req.body)
      const existingUser = await User.findOne({
        where: { email }
      });

      if (existingUser) return res.status(409).json({ message: 'User already exist!', name, email: existingUser.email });

      const savedUser = await User.create({
        email
      })
      res.status(201).json({
        message: 'User registered successfully!',
        savedUser
      });
    } catch (error) {
      res.status(400).json(`Error: ${error}`);
    }
  };

  static async updateUser(req, res) {
    try {
      const { name, email } = req.body;
      const existingUser = await User.findOne({
        where: { email }
      });

      if (!existingUser) return res.status(400).json({ message: 'User does not exist!', name, email });

      const updatedUser = await User.update({ name }, {
        name: 'Samuel Doe'
      });
      res.status(200).json({ message: 'User updated!', updatedUser });
    } catch (error) {
      res.status(400).json({ Error: error })
    };
  };
};

import { readdirSync } from 'fs';
import { basename as _basename, join } from 'path';
import Sequelize from 'sequelize';
import envConfigs from '../../config/config';

const basename = _basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = envConfigs[env];
const db = {};

let sequelize;
if (config.url) {
  sequelize = new Sequelize(config.url, config);
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

readdirSync(__dirname)
  .filter(file => {
    return (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js');
  })
  .forEach(file => {
    const model = require(join(__dirname, file))(sequelize, Sequelize.DataTypes)
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

export default db;

module.exports = (sequelize, DataTypes) => {
  const Comment = sequelize.define('Comment', {
    postId: DataTypes.INTEGER,
    comment: DataTypes.TEXT,
    userId: DataTypes.INTEGER
  }, {});
  Comment.associate = function(models) {
    // associations can be defined here
    Comment.belongsTo(models.User, {
      foreignKey: 'userId',
      as: 'author'
    });
    Comment.belongsTo(models.Post, {
      foreignKey: 'postId',
      as: 'post'
    });
  };
  return Comment;
};

module.exports = (sequelize, DataTypes) => {
  const Post = sequelize.define('Post', {
    title: DataTypes.STRING,
    content: DataTypes.TEXT,
    userId: DataTypes.INTEGER
  }, {});
  Post.associate = function(models) {
    // associations can be defined here
    Post.hasMany(models.Comment, {
      foreignKey: 'postId',
      as: 'comments',
      onDelete: 'CASCADE',
    });

    Post.belongsTo(models.User, {
      foreignKey: 'userId',
      as: 'author',
      onDelete: 'CASCADE',
    })
  };
  return Post;
};

module.exports = (sequelize, DataTypes) => {
  const User = sequelize.define('User', {
    name: DataTypes.STRING,
    email: DataTypes.STRING
  }, {});
  User.associate = function(models) {
    // associations can be defined here
    User.hasMany(models.Post, {
      foreignKey: 'userId',
      as: 'posts',
      onDelete: 'CASCADE',
    });

    User.hasMany(models.Comment, {
      foreignKey: 'userId',
      as: 'comments',
      onDelete: 'CASCADE',
    });
  };
  return User;
};
